import React, {useEffect, useState} from 'react';
import './App.css';

const App = () => {
  const APP_ID = "1eb5d637";
  const APP_KEY = "41f110548302c55cdabe03a9281347d9";
  const exampleReq = `https://api.edamam.com/search?q=onion&app_id=${APP_ID}&app_key=${APP_KEY}`;
  const [counter, setCounter] = useState(0);
  const hColor = document.getElementById('H').style.backgroundColor;

  useEffect(()=>{
    if(hColor === 'white'){
      hColor = 'red';
    } else {
      hColor = 'white';
    }
  },[counter])

  useEffect(()=>{
    hColor = 'white';
    // alert(document.getElementById('H').style.backgroundColor)

  }, [])

  return (
    <div className="App">
      <h1 id='H' onClick = {()=>{
        setCounter(counter + 1)
      }}>{counter}</h1>
    </div>
  );
}

export default App;
